   <div class="signin"> 

    <div class="content"> 

     <h2 class="login-logo text-center text-white">Result Management</h2> 

     <div class="form"> 

      <div class="inputBox"> 
        <input type="email" required v-model="userEmail"> <i>Email</i> 
      </div> 
      
      <div class="inputBox"> 
        <input type="password" required v-model="password"> <i>Password</i> 
      </div> 
      <div v-if="errors?.email">
        <p v-for="(error, index) in errors?.email" :key="index" class="text-danger">{{ error }}</p>
      </div>
      <div v-if="errors?.password">
          <p v-for="(error, index) in errors?.password" :key="index" class="text-danger">{{ error }}</p>
      </div>

      <div v-if="errorMessage">
        <p class="text-danger">{{ errorMessage }}</p>
      </div>


      <div class="inputBox"> 
        <button class="login-submit" v-if="auth.loading"><i class="fa-solid fa-spinner fa-spin"></i> Loading....</button> 
        <button class="login-submit" @click.prevent="submit" v-else>LogIn</button> 
